                                    "dictionary": [
                                        {
                                            "label": "裁切显示",
                                            "value": "cover"
                                        },
                                        {
                                            "label": "完全显示",
                                            "value": "contain"
                                        },
                                        {
                                            "label": "自定义",
                                            "value": "custom"
                                        }
                                    ],
                                    "display": "@[bgImgUrlError]"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "宽度",
                                    "bindKey": "bgSizeWidthError",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSizeError=='custom']"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "高度",
                                    "bindKey": "bgSizeHeightError",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSizeError=='custom']"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "block",
                                    "text": "平铺模式",
                                    "bindKey": "bgRepeatError",
                                    "dictionary": [
                                        {
                                            "label": "双向重复",
                                            "value": "repeat"
                                        },
                                        {
                                            "label": "水平重复",
                                            "value": "repeat-x"
                                        },
                                        {
                                            "label": "垂直重复",
                                            "value": "repeat-y"
                                        },
                                        {
                                            "label": "不重复",
                                            "value": "no-repeat"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
                                    "display": "@[bgImgUrlError]"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "inline",
                                    "text": "背景模式",
                                    "bindKey": "bgAttachmentError",
                                    "dictionary": [
                                        {
                                            "label": "固定",
                                            "value": "fixed"
                                        },
                                        {
                                            "label": "滚动",
                                            "value": "scroll"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
                                    "display": "@[bgImgUrlError]"
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "group",
                    "text": "结果联动",
                    "desc": "点击？查看结果联动设置用法指南",
                    "children": [
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "联动响应",
                            "bindKey": "resType",
                            "desc": "选择接收其他组件传值后要执行的后续动作，也就是选择接收到的值的作用是什么，处理完后返回控件所需要的结果",
                            "dictionary": [
                                {
                                    "label": "无",
                                    "value": "none"
                                },
                                {
                                    "label": "作为接口参数",
                                    "value": "interfaceParam"
                                },
                                {
                                    "label": "直接使用结果",
                                    "value": "resultVal"
                                },
                                {
                                    "label": "自定义函数",
                                    "value": "customFun"
                                }
                            ],
                            "default": "none",
                            "styleType": "radio"
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "参数名称",
                            "bindKey": "resInterfaceParam",
                            "default": "",
                            "display": "@[resType=='interfaceParam']"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "inline",
                            "text": "接口地址",
                            "bindKey": "resInterfaceUrl",
                            "default": "",
                            "display": "@[resType=='interfaceParam']"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "inline",
                            "text": "显示结果",
                            "bindKey": "resResultValField",
                            "desc": "根据接传值的数据格式指定结果集的字段，比如传的值为{data:张三}，则这里应该填写data，如果不填写则直接显示整个结果",
                            "default": "",
                            "display": "@[resType=='resultVal']"
                        },
                        {
                            "type": "actionSelect",
                            "layoutType": "block",
                            "text": "自定义函数",
                            "bindKey": "resFunction",
                            "desc": "其他组件传值后会把值传递到此方法并且调用，函数返回数据格式为：字符或数字（单选模式）、数组形式（多选或标签模式）",
                            "display": "@[resType=='customFun']",
                            "multiple": false
                        }
                    ]
                },
                {
                    "type": "group",
                    "text": "高级",
                    "desc": "点击？查看高级设置用法指南",
                    "children": [
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "表单标识",
                            "bindKey": "formFiledKey",
                            "desc": "如果为空则会提交表单时用唯一标识作为key",
                            "default": ""
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "分组标识",
                            "bindKey": "formGroupKey",
                            "desc": "表单分组标识，用于批量提交分组标识相同的表单控件值，减少提交控件一个一个选择控件的范围",
                            "default": "formGroupKey"
                        },
                        {
                            "type":"group",
                            "text":"回显与状态",
                            "desc":"用于回显控件的结果值与控件的状态控制，共四种状态：（编辑：default）、（禁用：disabled）、（只读：readonly）、（隐藏：hidden）",
                            "children":[
                                {
                                    "type": "input",
                                    "layoutType": "inline",
                                    "text": "结果集名",
                                    "bindKey": "echoDataName",
                                    "desc": "页面接口设定的结果集名称，位置为：页面设置》高级设置》页面接口",
                                    "default": "",
                                    "placeholder": "示例：resultData"
                                },
                                {
                                    "type": "select",
                                    "layoutType": "inline",
                                    "text": "回显方式",
                                    "bindKey": "echoType",
                                    "dictionary": [
                                        {
                                            "label": "同表单标识",
                                            "value": "equalFormKey"
                                        },
                                        {
                                            "label": "自定义字段",
                                            "value": "customFiled"
                                        },
                                        {
                                            "label": "自定义函数",
                                            "value": "customFunction"
                                        }
                                    ],
                                    "default": "equalFormKey"
                                },
                                {
                                    "type": "textarea",
                                    "layoutType": "inline",
                                    "text": "回显字段",
                                    "bindKey": "echoDataFiled",
                                    "desc": "根据接口返回数据格式指定结果集的字段，比如结果集名为resultData且它的值为{data:{filedName:'1000'}}，则这里应该填写data.filedName",
                                    "default": "",
                                    "placeholder": "示例：data.filedName 或者 [0].data[0].filedName",
                                    "display":"@[echoType=='customFiled']"
                                },
                                {
                                    "type": "actionSelect",
                                    "layoutType": "block",
                                    "text": "回显自定义函数",
                                    "bindKey": "echoFunction",
                                    "desc": "回显值时会调用此处设置的方法返回控件需要的回显值，接收参数格式为{...自定义的,...结果集名对象}",
                                    "multiple": false,
                                    "display":"@[echoType=='customFunction']"
                                },
                                {
                                    "type": "textarea",
                                    "layoutType": "inline",
                                    "text": "控件状态",
                                    "bindKey": "stateDataFiled",
                                    "desc": "主要用于控制控件的显示状态。根据接口返回数据格式指定结果集的字段，比如结果集名为resultData且它的值为{data:{filedName:'1000'}}，则这里应该填写data.filedName，值必须为指定的四种（编辑：default）、（禁用：disabled）、（只读：readonly）、（隐藏：hidden）",
                                    "default": "",
                                    "placeholder": "示例：data.filedName 或者 [0].data[0].filedName"
                                },
                                {
                                    "type": "actionSelect",
                                    "layoutType": "block",
                                    "text": "控件状态自定义函数",
                                    "bindKey": "stateFunction",
                                    "desc": "要用于控制控件的显示状态。加载时会调用此处设置的方法返回控件需要的控件状态值，接收参数格式为{...自定义的,...结果集名对象}，返回值必须为指定的四种（编辑：default）、（禁用：disabled）、（只读：readonly）、（隐藏：hidden）",
                                    "multiple": false
                                }
                            ]
                        }
                    ]
                }
            ]
        }
    ]
}

data.currentConfigText:
{
    "version": "1.0.0",
    "lasttime": "2021年8月12日18:46:37",
    "author": "申龙",
    "className": "forms",
    "module": [
        {
            "classId": "idm.componet.forms.iinput",
            "comName": "单行输入框",
            "className": "IInput",
            "comType": "common",
            "comLangue": "vue",
            "compositeAttr": [
                {
                    "type": "input",
                    "layoutType": "inline",
                    "text": "唯一标识",
                    "bindKey": "ctrlId",
                    "disabled": true,
                    "default": "@[packageid]",
                    "desc": "",
                    "helpUrl": ""
                },
                {
                    "type": "group",
                    "text": "基本属性",
                    "desc": "提供设置组件能达到展示效果的基本属性设置信息",
                    "children": [
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "标题",
                            "bindKey": "label",
                            "default": "单行输入框"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "显示标题",
                            "bindKey": "labelDisplay",
                            "default": true
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "提示文字",
                            "bindKey": "placeholder",
                            "placeholder": "请输入"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "inline",
                            "text": "默认值",
                            "bindKey": "defaultValue",
                            "default": ""
                        },
                        {
                            "type": "inputNumberUnit",
                            "layoutType": "inline",
                            "text": "最大宽度",
                            "bindKey": "maxWidth",
                            "dictionary": [
                                {
                                    "label": "auto",
                                    "value": "auto"
                                },
                                {
                                    "label": "px",
                                    "value": "px"
                                },
                                {
                                    "label": "%",
                                    "value": "%"
                                },
                                {
                                    "label": "rem",
                                    "value": "rem"
                                },
                                {
                                    "label": "em",
                                    "value": "em"
                                }
                            ],
                            "default": 0
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "标题排列",
                            "bindKey": "labelLayout",
                            "dictionary": [
                                {
                                    "label": "横向排列",
                                    "value": "horizontal"
                                },
                                {
                                    "label": "纵向排列",
                                    "value": "vertical"
                                }
                            ],
                            "default": "horizontal"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "尺寸",
                            "bindKey": "size",
                            "dictionary": [
                                {
                                    "label": "小",
                                    "value": "small"
                                },
                                {
                                    "label": "中",
                                    "value": "default"
                                },
                                {
                                    "label": "大",
                                    "value": "large"
                                }
                            ],
                            "default": "default"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "栅格布局",
                            "bindKey": "gridLayout",
                            "dictionary": [
                                {
                                    "label": "无",
                                    "value": "close"
                                },
                                {
                                    "label": "6:12",
                                    "value": "6:12"
                                },
                                {
                                    "label": "6:18",
                                    "value": "6:18"
                                },
                                {
                                    "label": "12:12",
                                    "value": "12:12"
                                }
                            ],
                            "default": "close",
                            "display": "@[labelLayout=='horizontal']"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "默认状态",
                            "bindKey": "defaultStatus",
                            "dictionary": [
                                {
                                    "label": "普通",
                                    "value": "default"
                                },
                                {
                                    "label": "禁用",
                                    "value": "disabled"
                                },
                                {
                                    "label": "只读",
                                    "value": "readonly"
                                },
                                {
                                    "label": "隐藏",
                                    "value": "hidden"
                                }
                            ],
                            "default": "default"
                        },
                        {
                            "type": "richText",
                            "layoutType": "inline",
                            "text": "描述内容",
                            "bindKey": "descContent"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "描述位置",
                            "bindKey": "descPosition",
                            "dictionary": [
                                {
                                    "label": "横向",
                                    "value": "horizontal"
                                },
                                {
                                    "label": "纵向",
                                    "value": "vertical"
                                }
                            ],
                            "default": "vertical",
                            "display": "@[gridLayout=='6:12']"
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "前缀内容",
                            "bindKey": "prefixContent",
                            "desc": "建议只填写一个字，超出将不能正常显示",
                            "default": ""
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "后缀内容",
                            "bindKey": "suffixContent",
                            "desc": "密码模式下无效不显示",
                            "default": ""
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "后缀方式",
                            "bindKey": "suffixType",
                            "dictionary": [
                                {
                                    "label": "直接显示",
                                    "value": "font"
                                },
                                {
                                    "label": "提示信息",
                                    "value": "tip"
                                }
                            ],
                            "default": "font",
                            "display": "@[suffixContent!='']"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "清除图标",
                            "bindKey": "clearIcon"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "密码模式",
                            "bindKey": "password"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "校验占位",
                            "desc":"是否预留校验信息空间，开启将会在底部占用25px空白预留给校验信息使用",
                            "bindKey": "retainBottomHeight",
                            "default": true
                        }
                    ]
                },
                {
                    "type": "group",
                    "text": "校验",
                    "desc": "提供校验输入内容格式设置",
                    "children": [
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "必填",
                            "bindKey": "required"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "必填错误提示",
                            "bindKey": "requiredText",
                            "default": "",
                            "display": "@[required]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "最小长度",
                            "bindKey": "minLength"
                        },
                        {
                            "type": "inputNumber",
                            "layoutType": "inline",
                            "text": "最小长度",
                            "bindKey": "minLengthNumber",
                            "maxLength": 99999,
                            "minLength": 0,
                            "default": 5,
                            "display": "@[minLength]"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "最小长度错误提示",
                            "bindKey": "minLengthText",
                            "default": "",
                            "display": "@[minLength]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "最大长度",
                            "bindKey": "maxLength"
                        },
                        {
                            "type": "inputNumber",
                            "layoutType": "inline",
                            "text": "最大长度",
                            "bindKey": "maxLengthNumber",
                            "maxLength": 99999,
                            "minLength": 0,
                            "default": 15,
                            "display": "@[maxLength]"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "最大长度错误提示",
                            "bindKey": "maxLengthText",
                            "default": "",
                            "display": "@[maxLength]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "邮箱",
                            "bindKey": "email"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "邮箱错误提示",
                            "bindKey": "emailText",
                            "default": "",
                            "display": "@[email]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "手机",
                            "bindKey": "mobile"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "手机错误提示",
                            "bindKey": "mobileText",
                            "default": "",
                            "display": "@[mobile]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "网址",
                            "bindKey": "url"
                        },
