versionList.configText:
{
    "version": "1.0.0",
    "lasttime": "2021年8月12日18:46:37",
    "author": "申龙",
    "className": "forms",
    "module": [
        {
            "classId": "idm.componet.forms.iinput",
            "comName": "单行输入框",
            "className": "IInput",
            "comType": "common",
            "comLangue": "vue",
            "compositeAttr": [
                {
                    "type": "input",
                    "layoutType": "inline",
                    "text": "唯一标识",
                    "bindKey": "ctrlId",
                    "disabled": true,
                    "default": "@[packageid]",
                    "desc": "",
                    "helpUrl": ""
                },
                {
                    "type": "group",
                    "text": "基本属性",
                    "desc": "提供设置组件能达到展示效果的基本属性设置信息",
                    "children": [
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "标题",
                            "bindKey": "label",
                            "default": "单行输入框"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "显示标题",
                            "bindKey": "labelDisplay",
                            "default": true
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "提示文字",
                            "bindKey": "placeholder",
                            "placeholder": "请输入"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "inline",
                            "text": "默认值",
                            "bindKey": "defaultValue",
                            "default": ""
                        },
                        {
                            "type": "inputNumberUnit",
                            "layoutType": "inline",
                            "text": "最大宽度",
                            "bindKey": "maxWidth",
                            "dictionary": [
                                {
                                    "label": "auto",
                                    "value": "auto"
                                },
                                {
                                    "label": "px",
                                    "value": "px"
                                },
                                {
                                    "label": "%",
                                    "value": "%"
                                },
                                {
                                    "label": "rem",
                                    "value": "rem"
                                },
                                {
                                    "label": "em",
                                    "value": "em"
                                }
                            ],
                            "default": 0
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "标题排列",
                            "bindKey": "labelLayout",
                            "dictionary": [
                                {
                                    "label": "横向排列",
                                    "value": "horizontal"
                                },
                                {
                                    "label": "纵向排列",
                                    "value": "vertical"
                                }
                            ],
                            "default": "horizontal"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "尺寸",
                            "bindKey": "size",
                            "dictionary": [
                                {
                                    "label": "小",
                                    "value": "small"
                                },
                                {
                                    "label": "中",
                                    "value": "default"
                                },
                                {
                                    "label": "大",
                                    "value": "large"
                                }
                            ],
                            "default": "default"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "栅格布局",
                            "bindKey": "gridLayout",
                            "dictionary": [
                                {
                                    "label": "无",
                                    "value": "close"
                                },
                                {
                                    "label": "6:12",
                                    "value": "6:12"
                                },
                                {
                                    "label": "6:18",
                                    "value": "6:18"
                                },
                                {
                                    "label": "12:12",
                                    "value": "12:12"
                                }
                            ],
                            "default": "close",
                            "display": "@[labelLayout=='horizontal']"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "默认状态",
                            "bindKey": "defaultStatus",
                            "dictionary": [
                                {
                                    "label": "普通",
                                    "value": "default"
                                },
                                {
                                    "label": "禁用",
                                    "value": "disabled"
                                },
                                {
                                    "label": "只读",
                                    "value": "readonly"
                                },
                                {
                                    "label": "隐藏",
                                    "value": "hidden"
                                }
                            ],
                            "default": "default"
                        },
                        {
                            "type": "richText",
                            "layoutType": "inline",
                            "text": "描述内容",
                            "bindKey": "descContent"
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "描述位置",
                            "bindKey": "descPosition",
                            "dictionary": [
                                {
                                    "label": "横向",
                                    "value": "horizontal"
                                },
                                {
                                    "label": "纵向",
                                    "value": "vertical"
                                }
                            ],
                            "default": "vertical",
                            "display": "@[gridLayout=='6:12']"
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "前缀内容",
                            "bindKey": "prefixContent",
                            "desc": "建议只填写一个字，超出将不能正常显示",
                            "default": ""
                        },
                        {
                            "type": "input",
                            "layoutType": "inline",
                            "text": "后缀内容",
                            "bindKey": "suffixContent",
                            "desc": "密码模式下无效不显示",
                            "default": ""
                        },
                        {
                            "type": "radio",
                            "layoutType": "inline",
                            "text": "后缀方式",
                            "bindKey": "suffixType",
                            "dictionary": [
                                {
                                    "label": "直接显示",
                                    "value": "font"
                                },
                                {
                                    "label": "提示信息",
                                    "value": "tip"
                                }
                            ],
                            "default": "font",
                            "display": "@[suffixContent!='']"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "清除图标",
                            "bindKey": "clearIcon"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "密码模式",
                            "bindKey": "password"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "校验占位",
                            "desc":"是否预留校验信息空间，开启将会在底部占用25px空白预留给校验信息使用",
                            "bindKey": "retainBottomHeight",
                            "default": true
                        }
                    ]
                },
                {
                    "type": "group",
                    "text": "校验",
                    "desc": "提供校验输入内容格式设置",
                    "children": [
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "必填",
                            "bindKey": "required"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "必填错误提示",
                            "bindKey": "requiredText",
                            "default": "",
                            "display": "@[required]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "最小长度",
                            "bindKey": "minLength"
                        },
                        {
                            "type": "inputNumber",
                            "layoutType": "inline",
                            "text": "最小长度",
                            "bindKey": "minLengthNumber",
                            "maxLength": 99999,
                            "minLength": 0,
                            "default": 5,
                            "display": "@[minLength]"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "最小长度错误提示",
                            "bindKey": "minLengthText",
                            "default": "",
                            "display": "@[minLength]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "最大长度",
                            "bindKey": "maxLength"
                        },
                        {
                            "type": "inputNumber",
                            "layoutType": "inline",
                            "text": "最大长度",
                            "bindKey": "maxLengthNumber",
                            "maxLength": 99999,
                            "minLength": 0,
                            "default": 15,
                            "display": "@[maxLength]"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "最大长度错误提示",
                            "bindKey": "maxLengthText",
                            "default": "",
                            "display": "@[maxLength]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "邮箱",
                            "bindKey": "email"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "邮箱错误提示",
                            "bindKey": "emailText",
                            "default": "",
                            "display": "@[email]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "手机",
                            "bindKey": "mobile"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "手机错误提示",
                            "bindKey": "mobileText",
                            "default": "",
                            "display": "@[mobile]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "网址",
                            "bindKey": "url"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "网址错误提示",
                            "bindKey": "urlText",
                            "default": "",
                            "display": "@[url]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "ip地址",
                            "bindKey": "ipAddress"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "ip地址错误提示",
                            "bindKey": "ipAddressText",
                            "default": "",
                            "display": "@[ipAddress]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "MAC地址",
                            "bindKey": "macAddress"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "MAC地址错误提示",
                            "bindKey": "macAddressText",
                            "default": "",
                            "display": "@[macAddress]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "自定义函数",
                            "bindKey": "customFun"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "函数代码",
                            "bindKey": "customFunCode",
                            "display": "@[customFun]",
                            "default": "function(value){return true}"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "自定义函数错误提示",
                            "bindKey": "customFunText",
                            "default": "",
                            "display": "@[customFun]"
                        },
                        {
                            "type": "switch",
                            "layoutType": "inline",
                            "text": "接口校验",
                            "bindKey": "interfaceVerify"
                        },
                        {
                            "type": "textarea",
                            "layoutType": "block",
                            "text": "接口地址",
                            "bindKey": "interfaceVerifyUrl",
                            "display": "@[interfaceVerify]",
                            "desc":"接口返回如果为true代表验证通过，为false代码验证未通过，接收参数格式为{...自定义的,currentVal:当前输入结果}"
                        },
                        {
                            "type": "group",
                            "text": "校验未通过设置",
                            "desc": "提供接口校验未通过的后续设置",
                            "display": "@[interfaceVerify]",
                            "children": [
                                {
                                    "type": "textarea",
                                    "layoutType": "block",
                                    "text": "未通过错误提示",
                                    "bindKey": "interfaceVerifyText",
                                    "default": ""
                                },
                                {
                                    "type": "switch",
                                    "layoutType": "inline",
                                    "text": "显示弹窗",
                                    "bindKey": "interfaceVerifyConfrim"
                                },
                                {
                                    "type": "textarea",
                                    "layoutType": "block",
                                    "text": "确认窗显示内容",
                                    "bindKey": "interfaceVerifyConfrimText",
                                    "placeholder":"例如：该组件已存在，是否要跳转至管理界面？",
                                    "default": ""
                                },
                                {
                                    "type": "actionSelect",
                                    "layoutType": "block",
                                    "text": "弹窗确认自定义函数",
                                    "bindKey": "interfaceVerifyConfrimOkAction",
                                    "desc": "确认框显示点击确定后会调用此处设置的函数，接收参数格式为{...自定义的,interfaceResultData:校验接口返回的结果}"
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "group",
                    "text": "样式设置",
                    "desc": "点击？查看样式设置用法指南",
                    "helpUrl": "",
                    "children": [
                        {
                            "type": "font",
                            "layoutType": "block",
                            "text": "标题文字",
                            "bindKey": "labelFont"
                        },
                        {
                            "type": "font",
                            "layoutType": "block",
                            "text": "错误提示文字",
                            "bindKey": "errorMsgFont"
                        },
                        {
                            "type": "font",
                            "layoutType": "block",
                            "text": "内容文字（编辑）",
                            "bindKey": "contentEditFont",
                            "desc": "编辑状态下的文字样式设置"
                        },
                        {
                            "type": "font",
                            "layoutType": "block",
                            "text": "内容文字（只读）",
                            "bindKey": "contentReadFont",
                            "desc": "只读情况下的文字样式设置"
                        },
                        {
                            "type": "group",
                            "text": "默认状态样式",
                            "desc": "文本框未获得焦点情况下的样式设置",
                            "helpUrl": "",
                            "children": [
                                {
                                    "type": "border",
                                    "layoutType": "block",
                                    "text": "边框设置",
                                    "bindKey": "borderDefault"
                                },
                                {
                                    "type": "colorPicker",
                                    "layoutType": "inline",
                                    "text": "背景色",
                                    "bindKey": "bgColor",
                                    "placeholder": "请选择颜色",
                                    "default": {}
                                },
                                {
                                    "type": "uploadImage",
                                    "layoutType": "inline",
                                    "text": "背景图片",
                                    "bindKey": "bgImgUrl",
                                    "placeholder": "可输入图片地址或直接上传"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "横向偏移",
                                    "bindKey": "positionX",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgImgUrl]"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "纵向偏移",
                                    "bindKey": "positionY",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgImgUrl]"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "block",
                                    "text": "背景大小",
                                    "bindKey": "bgSize",
                                    "dictionary": [
                                        {
                                            "label": "裁切显示",
                                            "value": "cover"
                                        },
                                        {
                                            "label": "完全显示",
                                            "value": "contain"
                                        },
                                        {
                                            "label": "自定义",
                                            "value": "custom"
                                        }
                                    ],
                                    "display": "@[bgImgUrl]"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "宽度",
                                    "bindKey": "bgSizeWidth",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSize=='custom']"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "高度",
                                    "bindKey": "bgSizeHeight",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSize=='custom']"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "block",
                                    "text": "平铺模式",
                                    "bindKey": "bgRepeat",
                                    "dictionary": [
                                        {
                                            "label": "双向重复",
                                            "value": "repeat"
                                        },
                                        {
                                            "label": "水平重复",
                                            "value": "repeat-x"
                                        },
                                        {
                                            "label": "垂直重复",
                                            "value": "repeat-y"
                                        },
                                        {
                                            "label": "不重复",
                                            "value": "no-repeat"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
                                    "display": "@[bgImgUrl]"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "inline",
                                    "text": "背景模式",
                                    "bindKey": "bgAttachment",
                                    "dictionary": [
                                        {
                                            "label": "固定",
                                            "value": "fixed"
                                        },
                                        {
                                            "label": "滚动",
                                            "value": "scroll"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
                                    "display": "@[bgImgUrl]"
                                }
                            ]
                        },
                        {
                            "type": "group",
                            "text": "焦点状态样式",
                            "desc": "文本框获得焦点情况下的样式设置",
                            "helpUrl": "",
                            "children": [
                                {
                                    "type": "border",
                                    "layoutType": "block",
                                    "text": "边框设置",
                                    "bindKey": "borderFocus"
                                },
                                {
                                    "type": "colorPicker",
                                    "layoutType": "inline",
                                    "text": "背景色",
                                    "bindKey": "bgColorFocus",
                                    "placeholder": "请选择颜色",
                                    "default": {}
                                },
                                {
                                    "type": "uploadImage",
                                    "layoutType": "inline",
                                    "text": "背景图片",
                                    "bindKey": "bgImgUrlFocus",
                                    "placeholder": "可输入图片地址或直接上传"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "横向偏移",
                                    "bindKey": "positionXFocus",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgImgUrlFocus]"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "纵向偏移",
                                    "bindKey": "positionYFocus",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgImgUrlFocus]"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "block",
                                    "text": "背景大小",
                                    "bindKey": "bgSizeFocus",
                                    "dictionary": [
                                        {
                                            "label": "裁切显示",
                                            "value": "cover"
                                        },
                                        {
                                            "label": "完全显示",
                                            "value": "contain"
                                        },
                                        {
                                            "label": "自定义",
                                            "value": "custom"
                                        }
                                    ],
                                    "display": "@[bgImgUrlFocus]"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "宽度",
                                    "bindKey": "bgSizeWidthFocus",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSizeFocus=='custom']"
                                },
                                {
                                    "type": "inputNumberUnit",
                                    "layoutType": "inline",
                                    "text": "高度",
                                    "bindKey": "bgSizeHeightFocus",
                                    "dictionary": [
                                        {
                                            "label": "px",
                                            "value": "px"
                                        },
                                        {
                                            "label": "%",
                                            "value": "%"
                                        },
                                        {
                                            "label": "em",
                                            "value": "em"
                                        }
                                    ],
                                    "display": "@[bgSizeFocus=='custom']"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "block",
                                    "text": "平铺模式",
                                    "bindKey": "bgRepeatFocus",
                                    "dictionary": [
                                        {
                                            "label": "双向重复",
                                            "value": "repeat"
                                        },
                                        {
                                            "label": "水平重复",
                                            "value": "repeat-x"
                                        },
                                        {
                                            "label": "垂直重复",
                                            "value": "repeat-y"
                                        },
                                        {
                                            "label": "不重复",
                                            "value": "no-repeat"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
                                    "display": "@[bgImgUrlFocus]"
                                },
                                {
                                    "type": "radio",
                                    "layoutType": "inline",
                                    "text": "背景模式",
                                    "bindKey": "bgAttachmentFocus",
                                    "dictionary": [
                                        {
                                            "label": "固定",
                                            "value": "fixed"
                                        },
                                        {
                                            "label": "滚动",
                                            "value": "scroll"
                                        },
                                        {
                                            "label": "继承",
                                            "value": "inherit"
                                        }
                                    ],
